from pprint import pprintimport json# I've created a JSON file of my manual data input in Notepad++ and then put it in the folder. Going to pull itwith open('Rush_Album_Lyrics.json', 'r') as outfile:        rush_solo = outfile.read()rush_solo = json.loads(rush_solo)# Creating a rush songs variablerush_songs = rush_solo['albums']['songs']# Display each song and the rough word count of each, based on splitting the strings. Words like Im count as a single word.for idx, val in enumerate(rush_songs):	print(val['song name'])	print(len(val['song lyrics'].split()))